sp_widget.client_script:
function($scope) {  
  var c = this;  

$scope.submit = function(){
	if(!c.data.src){
	swal("Signature Error", "Please sign, and save your signature", "error");
	}
	else if ((!c.data.firstName)||(!c.data.lastName)||(!c.data.phone)||(!c.data.email)||(!c.data.dob)||(!c.data.location)||(!c.data.manager_first)||(!c.data.manager_last)||(!c.data.manager_phone)||(!c.data.manager_email)){
		swal("Missing Information", "Please complete the form before submitting", "error");
	}
	else if ((c.data.role == 'Community Provider Office Staff')&&((!c.data.practice_manager)||(!c.data.practice_manager_email))){
		swal("Missing Information", "Please complete the form before submitting", "error");
	}
	else{
		c.server.update();
	swal("Thank you", "Your request has been submitted.","success").then(function () {
		var url = 'https://www.honorhealth.com/';
	top.window.location = url;

	});
	}
}
}

		//your approver/manager will need to approve within 7 business days or your request will be cancelled.

